INSERT INTO `product` VALUES (1, 'MÁY LỌC NƯỚC KAROFI N-e239', 2, '<div class=\"product-info__slide\">\r\n<div>\r\n    <p style=\"text-align: center;\"><strong style=\"color: #00aae7; font-size: 25.92px;\"><span style=\"font-size: 25.92px;\">MÁY LỌC NƯỚC KAROFI KAQ-U95 AioTec</span></strong></p>\r\n</div>\r\n<div style=\"text-align: left;\">\r\n    <p><span style=\"font-size: 110%; color: #000000;\">Là dòng máy lọc nước không tủ đầu tiên tích hợp tính năng <strong>AioTec</strong> ( Internet of Things ), kết nối hoàn hảo tới chiếc điện thoại di động thông minh hàng ngày bạn vẫn đang sử dụng.</span></p>\r\n    <p><span style=\"font-size: 110%; color: #000000;\">Không chỉ cảnh báo tự động, dòng máy lọc nước thông minh <strong>Karofi KAQ-U95</strong> còn là một trong những sản phẩm “thời thượng”. Bạn hoàn toàn yên tâm khi</span></p>\r\n    <p><span style=\"font-size: 110%; color: #000000;\">– Máy gặp trục trặc, sự cố</span><br>\r\n        <span style=\"font-size: 110%; color: #000000;\">– Lõi lọc bẩn, đến lúc phải thay thế</span><br>\r\n        <span style=\"font-size: 110%; color: #000000;\">– Thời gian hoạt động</span>\r\n    </p>\r\n</div>\r\n\r\n<h3 style=\"text-align: left;\"><span style=\"color: #000000;\"><strong><span id=\"kiem-soat-chat-luong-thong-minh-chu-dong-nho-cong-nghe-aiotec\">Kiểm soát chất lượng thông minh chủ động nhờ công nghệ AioTec</span></strong></span></h3>\r\n<ul style=\"text-align: left;\">\r\n    <li><span style=\"font-size: 110%; color: #000000;\">Quản lý thời gian sử dụng lõi</span></li>\r\n    <li><span style=\"font-size: 110%; color: #000000;\">Kiểm tra độ sạch của nước từ xa, thông qua điện thoại thông minh</span></li>\r\n    <li><span style=\"font-size: 110%; color: #000000;\">Kích hoạt bảo hành chính hãng, liên hệ trực tiếp</span></li>\r\n</ul>\r\n\r\n<h3 class=\"product-info__slide-main__item slide-video\"><span style=\"color: #000000;\"><strong><span id=\"he-thong-10-loi-loc-manh-me-hoan-toan-moi\">Hệ thống 10 lõi lọc mạnh mẽ hoàn toàn mới</span></strong></span></h3>\r\n<ul style=\"text-align: left;\">\r\n    <li><span style=\"font-size: 110%; color: #000000;\">Bộ lõi lọc chức năng SMAX hiệu suất cao, sử dụng công nghệ đúc nguyên khối</span></li>\r\n    <li><span style=\"font-size: 110%; color: #000000;\">Tăng lưu lượng nước đầu ra.</span></li>\r\n    <li><span style=\"font-size: 110%; color: #000000;\">Tối ưu hoá các cút nối giảm thiểu vấn đề rò rỉ và tái nhiễm khuẩn nước.</span></li>\r\n    <li><span style=\"font-size: 110%; color: #000000;\">Tiết kiệm chi phí và thời gian thay thế các lõi lọc chức năng.</span></li>\r\n    <li><span style=\"font-size: 110%; color: #000000;\">Dễ dàng thay thế tại nhà, tái tạo nước tinh khiết với lượng khoáng chất cao nhất.</span></li>\r\n</ul>\r\n<h3><strong><span style=\"font-size: 90%; color: #000000;\"><span id=\"bo-sung-khoang-chat-tot-qua-6-loi-chuc-nang\">Bổ sung khoáng chất tốt qua 6 lõi chức năng</span></span></strong></h3>\r\n<ul>\r\n    <li style=\"text-align: left;\"><span style=\"font-size: 110%; color: #000000;\">Tạo nước Hydrogen hoạt tính bằng phương pháp tự nhiên, hỗ trợ ngăn ngừa lão hoá.</span></li>\r\n    <li style=\"text-align: left;\"><span style=\"font-size: 110%; color: #000000;\">Bổ sung khoáng chất K, Na…cân bằng pH giúp nước ngon ngọt hơn.</span></li>\r\n    <li style=\"text-align: left;\"><span style=\"font-size: 110%; color: #000000;\">Lõi lọc Tourmaline mới giúp hoạt hoá, chia nhỏ phân tử nước giúp nước dễ dàng thẩm thấu vào cơ thể khi cần bù nước nhanh.</span></li>\r\n    <li style=\"text-align: left;\"><span style=\"font-size: 110%; color: #000000;\">Lõi lọc Nano bạc kháng khuẩn tốt nhất thị trường.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"color: #000000;\"><strong>MÁY THÍCH HỢP LẮP TRONG CÁC KHÔNG GIAN NHỎ GỌN</strong></span></p>\r\n\r\n<h3 class=\"product-info__slide-main__item slide-video\" style=\"text-align: center;\"><strong>NƯỚC UỐNG TRƯỢC TIẾP (TIÊU CHUẨN QCVN06-1 BỘ Y TẾ)</strong></h3>\r\n\r\n<p style=\"text-align: center;\"><strong><span style=\"font-size: 150%; color: #00aae7;\">PHỐI CẢNH VỊ TRÍ LẮP ĐẶT</span></strong></p>\r\n\r\n</div>', '\"Vi Điều Khiển\":\"Thông minh – AioTec kết nối Wifi\" | \"Chức năng\":\"Cảnh báo thay lõi, chất lượng nước sau lọc\" | \"Hệ thông cấp lọc\":\"10 cấp – Hydrogen gấp đôi\" | \"Công suất lọc\":\"20 Lít/giờ\" | \"Màng lọc RO\":\"Furifim Nhập Khẩu từ Mỹ\" | \"Chất Liệu Tủ\":\"Khung thép nhỏ gọn\" | \"Bình áp\":\"Nhựa 8.5L\" | \"Tiêu thụ điện \":\"24w/h\" | \"Kích thước tủ\":\"(304 x 209 x 436) mm\" | \"Bảo hành\":\"36 tháng\" | \"Nước lọc\":\"Đạt chuẩn QCVN06-1 BYT uống trực tiếp\" | \"Xuất xứ\":\"Việt Nam\" | \"Giá niêm yết từ\":\"6,590,000 VNĐ\" | \"Giá bán\":\"Liên Hệ\" | \"Lắp đặt\":\"0937-557-555 (Miễn Phí)\"', 5990000);
